import React, { Component } from "react";
import axios from "axios";
import Banner from "../components/Banner";
import AricleItem from "../components/ArticleItem";
import { Container } from "@material-ui/core";

class ArticleItemPage extends Component {
  state = { article: {} };
  componentDidMount() {
    this.getArticleItem();
  }
  getArticleItem = () => {
    axios
      .get(`https://jsonplaceholder.typicode.com/photos/1`)
      .then((response) => {
        this.setState({ article: response.data });
      });
  };
  render() {
    console.log(this.props.match.params.id);
    return (
      <div>
        <Banner />
        <Container>
          <AricleItem className="article-item" article={this.state.article} />
        </Container>
      </div>
    );
  }
}

export default ArticleItemPage;
