import React, { Component } from "react";
import axios from "axios";
import Banner from "../components/Banner";
import AricleItem from "../components/ArticleItem";
import { Container, Grid } from "@material-ui/core";

class ArticleItemPage extends Component {
  state = { articles: [], article: {} };
  componentDidMount() {
    this.getArticleItem();
    this.getArticles();
  }
  getArticleItem = () => {
    axios
      .get(
        `https://jsonplaceholder.typicode.com/photos/${this.props.match.params.id}`
      )
      .then((response) => {
        this.setState({ article: response.data });
      });
  };
  getArticles = (e, value = 1) => {
    this.setState({ page: value });
    axios
      .get(`https://jsonplaceholder.typicode.com/photos?_limit=5`)
      .then((response) => {
        this.setState({ articles: response.data });
      });
  };
  render() {
    return (
      <div>
        <Banner />
        <div className="article-item-layout">
          <Container>
            <Grid container spacing={3}>
              <Grid item xs={12} md={8}>
                <AricleItem
                  className="article-item"
                  article={this.state.article}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <AricleItem
                  className="article-item-home-card"
                  article={this.state.article}
                />
              </Grid>
            </Grid>
          </Container>
        </div>
      </div>
    );
  }
}

export default ArticleItemPage;
