import React, { Component } from "react";
import Banner from "../components/Banner";
import { TextField, Container } from "@material-ui/core";
import { useForm } from "react-hook-form";

function SampleForm() {
  const { register, handleSubmit } = useForm();
  const onSubmit = (data) => {
    alert(JSON.stringify(data));
  };

  return (
    <div className="App">
      <form onSubmit={handleSubmit(onSubmit)}>
        <div>
          <TextField
            fullWidth
            name="firstName"
            placeholder="bill"
            label="Your Name"
            variant="outlined"
            className="mb-3"
            ref={register}
          />
        </div>

        <div>
          <TextField
            fullWidth
            name="lastName"
            placeholder="Your Email"
            variant="outlined"
            className="mb-3"
            ref={register}
          />
        </div>

        <div>
          <input
            name="email"
            placeholder="bluebill1049@hotmail.com"
            type="email"
            variant="outlined"
            className="mb-3"
            ref={register}
            variant="outlined"
            className="mb-3"
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

