import React, { Component } from "react";
import Banner from "../components/Banner";
import { TextField, Container } from "@material-ui/core";
import { useForm } from "react-hook-form";
class Contact extends Component {
  render() {
    const { handleSubmit, register, errors } = useForm();
    const onSubmit = (values) => console.log(values);
    return (
      <React.Fragment>
        <Banner title="contact us" />
        <Container>
          <form onSubmit={handleSubmit(onSubmit)}>
            <TextField
              fullWidth
              id="outlined-basic"
              label="Your Name"
              variant="outlined"
              className="mb-3"
            />
            <TextField
              fullWidth
              id="outlined-basic"
              label="Your Email"
              variant="outlined"
              className="mb-3"
              name="email"
              ref={register({
                required: "Required",
                pattern: {
                  value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                  message: "invalid email address",
                },
              })}
            />
            <TextField
              fullWidth
              id="outlined-basic"
              label="subject"
              variant="outlined"
              className="mb-3"
            />
            <TextField
              fullWidth
              multiline
              id="outlined-basic"
              label="Message"
              variant="outlined"
              className="mb-3"
              rows={4}
            />
          </form>
        </Container>
      </React.Fragment>
    );
  }
}
export default Contact;
