import React, { Component } from "react";
import axios from "axios";
import Banner from "../components/Banner";
import AricleItem from "../components/ArticleItem";
import { Container, Grid } from "@material-ui/core";

class ArticleItemPage extends Component {
  state = { article: {} };
  componentDidMount() {
    this.getArticleItem();
  }
  getArticleItem = () => {
    axios
      .get(
        `https://jsonplaceholder.typicode.com/photos/${this.props.match.params.id}`
      )
      .then((response) => {
        this.setState({ article: response.data });
      });
  };
  render() {
    return (
      <div>
        <Banner />
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <AricleItem className="article-item" article={this.state.article} />
          </Grid>
        </Grid>
      </div>
    );
  }
}

export default ArticleItemPage;
