import React, { Component } from "react";
import axios from "axios";
import Banner from "../components/Banner";
import AricleItem from "../components/ArticleItem";
import { Container, Grid } from "@material-ui/core";
import { Link } from "react-router-dom";

class Articles extends Component {
  state = { articles: [], article: {} };
  componentDidMount() {
    this.getArticleItem();
    this.getArticles();
  }
  getArticleItem = () => {
    console.log(this.props.match.path);
    axios
      .get(
        `https://jsonplaceholder.typicode.com/photos/${this.props.match.params.id}`
      )
      .then((response) => {
        this.setState({ article: response.data });
      });
    // window.location.reload();
  };
  getArticles = () => {
    axios
      .get(`https://jsonplaceholder.typicode.com/photos?_page=2&_limit=10`)
      .then((response) => {
        this.setState({ articles: response.data });
      });
  };
  render() {
    return (
      <div>
        <Banner title="Articles" />
        <div className="article-item-layout">
          <Container>
            <Grid container spacing={3}>
              {this.state.articles.map((article) => (
                <Grid item xs={12} md={4}>
                  <Link to={"/articles/" + article.id} key={article.id}>
                    <AricleItem
                      className="article-item-single"
                      article={article}
                      key={article.id}
                      smallCard={true}
                      onClick={this.getArticleItem}
                    />
                  </Link>
                </Grid>
              ))}
            </Grid>
          </Container>
        </div>
      </div>
    );
  }
}

export default Articles;
