import React, { Component } from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import axios from "axios";
import "./App.css";
import { v4 as uuidv4 } from "uuid";

class App extends Component {
  state = {
    todos: [
      // { id: uuidv4(), title: "Run your life like a pro", completed: false },
      // { id: uuidv4(), title: "Your to-do list. Done.", completed: false },
      // { id: uuidv4(), title: "Work better together", completed: false },
    ],
  };

  componentDidMount() {
    axios
      .get("https://jsonplaceholder.typicode.com/todos?_limit=10")
      .then((response) => {
        this.setState({ todos: response.data });
      });
  }

  markComplete = (id) => {
    let todos = this.state.todos.map((todo) => {
      if (todo.id == id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    this.setState({ todos });
  };

  deleteTodo = (id) => {
    // let todos = [...this.state.todos];
    // let index;
    // todos.map((todo) => {
    //   if (todo.id == id) {
    //     index = todos.indexOf(todo);
    //   }
    // });
    // todos.splice(index, 1);
    // this.setState({ todos });

    axios
      .delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then((response) => {
        let todos = [...this.state.todos].filter((todo) => todo.id !== id);
        this.setState({ todos: todos });
      });
  };

  addTodo = (title) => {
    // let todos = [...this.state.todos];
    // todos.push({
    //   id: uuidv4(),
    // title,
    // completed: false,
    // });
    // this.setState({ todos });

    let newTodo = { title, completed: false };

    axios
      .post("https://jsonplaceholder.typicode.com/todos", newTodo)
      .then((response) => {
        this.setState({ todos: [...this.state.todos, response.data] });
        let ele = document.getElementById("app");
        ele.scrollIntoView({
          behavior: "smooth",
          block: "end",
          inline: "nearest",
        });
      });
  };

  render() {
    return (
      <Router>
        <div className="App" id="app">
          <Layout />
          <div className="container">
            <Route
              exact
              path="/react-app"
              render={(props) => (
                <React.Fragment>
                  <AddTodo addTodo={this.addTodo} />
                  <Todos
                    todos={this.state.todos}
                    markComplete={this.markComplete}
                    deleteTodo={this.deleteTodo}
                  />
                </React.Fragment>
              )}
            />

            <Route path="/react-app/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
