import React, { Component } from "react";
import Banner from "../components/Banner";
import { TextField, Container, Button } from "@material-ui/core";
import { useForm, Controller } from "react-hook-form";

function SampleForm() {
  const { register, handleSubmit, errors, control } = useForm();
  //   const onSubmit = (data) => {
  //     alert(JSON.stringify(data));
  //   };
  const onSubmit = (values) => console.log(values);

  return (
    <div className="App">
      <form onSubmit={handleSubmit(onSubmit)}>
        <Controller
          as={TextField}
          name="firstName"
          control={control}
          defaultValue=""
        />
        {errors}

        <TextField
          fullWidth
          id="outlined-basic"
          label="Your Name"
          variant="outlined"
          className="mb-3"
        />
        {/* <input
          name="email"
          ref={register({
            required: "Required",
            pattern: {
              value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
              message: "invalid email address",
            },
          })}
        />
        {errors.email && errors.email.message} */}

        <TextField
          fullWidth
          id="outlined-basic"
          label="Your Email"
          variant="outlined"
          className="mb-3"
          name="email"
          ref={register({
            required: "Required",
            pattern: {
              value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
              message: "invalid email address",
            },
          })}
        />
        {errors.email && errors.email.message}

        <TextField
          fullWidth
          id="outlined-basic"
          label="subject"
          variant="outlined"
          className="mb-3"
        />
        <TextField
          fullWidth
          multiline
          id="outlined-basic"
          label="Message"
          variant="outlined"
          className="mb-3"
          rows={4}
        />
        <Button type="submit" variant="contained" color="primary">
          Submit
        </Button>
      </form>
    </div>
  );
}

class Contact extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <React.Fragment>
        <Banner title="contact us" />
        <Container>
          <SampleForm />
        </Container>
      </React.Fragment>
    );
  }
}
export default Contact;
