import React, { Component } from "react";
import axios from "axios";
import { Container, Typography } from "@material-ui/core";
import Pagination from "@material-ui/lab/Pagination";
import AricleItem from "../components/ArticleItem";
class ArticleSection extends Component {
  state = { articles: [], page: 1 };
  componentDidMount() {
    this.getArticles();
  }
  getArticles = (e, value) => {
    axios
      .get("https://jsonplaceholder.typicode.com/photos?_page=1&_limit=5")
      .then((response) => {
        this.setState({ articles: response.data });
      });
  };
  handleChange = (e, value) => {
    this.setState({ page: value });
    axios
      .get(
        `https://jsonplaceholder.typicode.com/photos?_page=${value}&_limit=5`
      )
      .then((response) => {
        this.setState({ articles: response.data });
      });
  };
  render() {
    return (
      <div className="article-section">
        <Typography>Page: {this.state.page}</Typography>
        <Pagination
          count={10}
          page={this.state.page}
          onChange={this.handleChange}
        />

        <Container>
          {this.state.articles.map((article) => (
            <AricleItem
              className="article-item-home-card"
              article={article}
              key={article.id}
            />
          ))}
        </Container>
      </div>
    );
  }
}

export default ArticleSection;
